//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./interfaces/ICodeTrust.sol";

/**
 * @title Code Trust
 * @author Miguel Gomez Carpena
 */
contract CodeTrust is ICodeTrust {
    //     [truster -->       contract] --> expiration
    mapping(address => mapping(address => uint256)) private trustedCodeExp;

    function trustCodeAt(address trustedCode, uint256 duration) external {
        if (msg.sender == tx.origin) {
            // the sender is an EOA (31536000 sec = 1 year)
            require(
                duration >= 10 && duration <= 31536000,
                "Invalid duration, check Doc"
            );
            trustedCodeExp[msg.sender][trustedCode] =
                block.timestamp +
                duration;
        } else {
            // the sender is another contract
            if (duration < 3) {
                duration = 1;
                trustedCodeExp[msg.sender][trustedCode] = duration;
            } else {
                trustedCodeExp[msg.sender][trustedCode] =
                    block.timestamp +
                    duration;
            }
        }
    }

    function untrustCodeAt(address trustedCode) external {
        uint256 actualExpiration = trustedCodeExp[msg.sender][trustedCode];
        // or it has no expiration (infinite) OR it has expiration and is further than the current timestamp
        require(
            actualExpiration == 1 || actualExpiration > block.timestamp + 5,
            "Already expired"
        );
        trustedCodeExp[msg.sender][trustedCode] = block.timestamp;
    }

    function isTrustedCode(
        address trustedContract,
        address by,
        uint256 extTimestamp
    ) external view returns (bool) {
        if (extTimestamp == 0) {
            extTimestamp = block.timestamp;
        }
        // set sender by default
        if (by == address(0)) {
            by = msg.sender;
        }
        uint256 actualExpiration = trustedCodeExp[by][trustedContract];
        // or it has no expiration (infinite) OR it has expiration and is further than the current timestamp
        if (actualExpiration == 1 || actualExpiration > extTimestamp + 5) {
            return true;
        } else {
            return false;
        }
    }
}

contracts/DumbExample.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.0 <0.9.0;

import "./Trustable.sol";

/**
 * @title Code Trust
 * @author Miguel Gomez Carpena
 */
contract DumbExample is Trustable {
  constructor(ICodeTrust systemCodeTrust) {
    _codeTrust = systemCodeTrust;
  }

  function trustOneContract(address contractToTrust) public {
    _codeTrust.trustCodeAt(contractToTrust, 0);
  }

  function checkIfTrusted(address contractToCheck) public view returns (bool trusted) {
    return _codeTrust.isTrustedCode(contractToCheck, address(0), 0); 
  }
}

contracts/interfaces/ICodeTrust.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

/**
 * @title (Interface) Code Trust
 * @author Miguel Gomez Carpena
 * @dev Interface of contract CodeTrust
 */
interface ICodeTrust {
  /**
   * @notice any sender can use this method to trust other address (should be a contract address)
   * @dev it checks if the address is a contract address or a EOA comparing the msg.sender and tx.origin to force having duration or not
   * @param trustedCode the address of the contract address to be trusted
   * @param duration (optional) [1] datetime to trust this contract code. Mandatory if this call comes from a EOA. If sender is another contract, can be 1 meaning infinite duration
   */
  function trustCodeAt(address trustedCode, uint256 duration) external;

  /**
   * @notice simple method to explicitly untrust a previously trusted contract
   * @param trustedCode the address of the contract to be untrusted
   */
  function untrustCodeAt(address trustedCode) external;

  /**
   * @notice method to check if a contract is trusted by any address
   * @param trustedCode the address of the contract to be checked
   * @param by (optional) [sender] the address of the one that is trusting the contract or not (truster?? X-))
   * @param extTimestamp (optional) [block.timestamp] external timestamp to use as time reference
   * @return bool wheater the contract is trusted by "by" address or not
   */
  function isTrustedCode(
    address trustedCode,
    address by,
    uint256 extTimestamp
  ) external view returns (bool);
}

